#ifndef OPLUS_TFA9865_REG_H
#define OPLUS_TFA9865_REG_H

/* TFA9865_STATUS_FLAGS0 */
/* Over Temperature */
#define TFA9865_STATUS_FLAGS0_OTP_POS 1
#define TFA9865_STATUS_FLAGS0_OTP_VAL 1
#define TFA9865_STATUS_FLAGS0_OTP_MASK (0x1 << (TFA9865_STATUS_FLAGS0_OTP_POS))

/* UVP */
#define TFA9865_STATUS_FLAGS0_UVP_POS 2
#define TFA9865_STATUS_FLAGS0_UVP_VAL 1
#define TFA9865_STATUS_FLAGS0_UVP_MASK (0x1 << (TFA9865_STATUS_FLAGS0_UVP_POS))

/* OVP */
#define TFA9865_STATUS_FLAGS0_OVP_POS 3
#define TFA9865_STATUS_FLAGS0_OVP_VAL 1
#define TFA9865_STATUS_FLAGS0_OVP_MASK (0x1 << (TFA9865_STATUS_FLAGS0_OVP_POS))

/* Over Current */
#define TFA9865_STATUS_FLAGS0_OCP_POS 4
#define TFA9865_STATUS_FLAGS0_OCP_VAL 0
#define TFA9865_STATUS_FLAGS0_OCP_MASK (0x1 << (TFA9865_STATUS_FLAGS0_OCP_POS))

#define TFA9865_STATUS_FLAGS0_MASK (\
	TFA9865_STATUS_FLAGS0_OTP_MASK |\
	TFA9865_STATUS_FLAGS0_UVP_MASK |\
	TFA9865_STATUS_FLAGS0_OVP_MASK |\
	TFA9865_STATUS_FLAGS0_OCP_MASK)

#define TFA9865_STATUS_FLAGS0_EXPECT_VALUE (\
	(TFA9865_STATUS_FLAGS0_OTP_VAL << TFA9865_STATUS_FLAGS0_OTP_POS) |\
	(TFA9865_STATUS_FLAGS0_UVP_VAL << TFA9865_STATUS_FLAGS0_UVP_POS) |\
	(TFA9865_STATUS_FLAGS0_OVP_VAL << TFA9865_STATUS_FLAGS0_OVP_POS) |\
	(TFA9865_STATUS_FLAGS0_OCP_VAL << TFA9865_STATUS_FLAGS0_OCP_POS))

/* TFA9865_STATUS_FLAGS1 */
#define TFA9865_STATUS_FLAGS1_CLKS_POS 0
#define TFA9865_STATUS_FLAGS1_CLKS_VAL 1
#define TFA9865_STATUS_FLAGS1_CLKS_MASK (0x1 << (TFA9865_STATUS_FLAGS1_CLKS_POS))

#define TFA9865_STATUS_FLAGS1_CLIPS_POS 12
#define TFA9865_STATUS_FLAGS1_CLIPS_VAL 0
#define TFA9865_STATUS_FLAGS1_CLIPS_MASK (0x1 << (TFA9865_STATUS_FLAGS1_CLIPS_POS))

#define TFA9865_STATUS_FLAGS1_MASK (\
	TFA9865_STATUS_FLAGS1_CLKS_MASK |\
	TFA9865_STATUS_FLAGS1_CLIPS_MASK)

#define TFA9865_STATUS_FLAGS1_EXPECT_VALUE (\
	(TFA9865_STATUS_FLAGS1_CLKS_VAL << TFA9865_STATUS_FLAGS1_CLKS_POS) |\
	(TFA9865_STATUS_FLAGS1_CLIPS_VAL << TFA9865_STATUS_FLAGS1_CLIPS_POS))

#endif /* OPLUS_TFA9865_REG_H */

